                new EncyclopediaEntity {Id = new Guid("00000000-0000-0000-0000-000000000051"), TitleEnUs = "Lung cancer remission", TitleUkUa = "Ремісія легенів" },
                new EncyclopediaEntity {Id = new Guid("00000000-0000-0000-0000-000000000052"), TitleEnUs = "Asthma", TitleUkUa = "Астма", DescriptionEnUs = "A long-term disease of the airways, leading to attacks of wheezing, coughing, chest tightness, and shortness of breath. Outside such attacks, breathing is normal. Often it is manifested in childhood and has allergic cause."},
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000053"), TitleEnUs = "Other", TitleUkUa = "Інше"},
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000054"), TitleEnUs = "Kidney failure", DescriptionEnUs = "When kidneys fail completely, our blood accumulates large amounts of garbage products, which are normalle removed with the urine through the kidneys. In this case, apart from translantation, only dialysis can save life. Dialysis is a process of blood cleaning performed either by regular blood processing through a dialysis machine or (rarely) by pumping a dialysis fluid in and out of the abdominal cavity." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000055"), TitleEnUs = "Kidney infections", DescriptionEnUs = "Chronic inflammation of the kidneys, leading to kindey failure." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000056"), TitleEnUs = "Chronic kidney disease", DescriptionEnUs = "Advanced dysfunction of the kidneys due to inflammation, infection, diabetes, high-blood pressure, medicines, genetic disease and others. The level of kidney failure is determined routinely by the level of creatinine. Stage 5 requires dialysis." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000057"), TitleEnUs = "Polycystic kidney disease", DescriptionEnUs = "A genetic disease, leading to formation of large cysts in the kidneys and leading to kidney failure." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000058"), TitleEnUs = "Kidney stones", DescriptionEnUs = "They (a.k.a. urolithiasis) are solid stone-like structures, formed in the urine-draining parts of the kidney. They can be transported wonstream the urinary flow to the urinary bladder and sometimes leave the body spontaneously with the urine (seldom)." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000059"), TitleEnUs = "Bladder infections", DescriptionEnUs = "Chronic inflammation of the urinary bladder with symptoms of recurrent pain and/or frequent urge to urinate." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000060"), TitleEnUs = "Removed kidney", TitleUkUa = "Видалена нирка"},
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000061"), TitleEnUs = "Artial fibrillation (Afib)", DescriptionEnUs = "Disturbances in the regular rhythmic function of the heart. Most commonly, it includes atrial fibrillation, abbreviated often as Afib." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000062"), TitleEnUs = "Valve disease", DescriptionEnUs = "Diseases of the heart valves, which can be harmless (heart murmur, heart noise), but can be also serious." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000063"), TitleEnUs = "High blood pressure", DescriptionEnUs = "Elevation of the blood pressure above 130/80. Even if blood pressure remains normal most of the time, individually elevated values (when not exercising) speak about high blood pressure disease." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000064"), TitleEnUs = "Angina", TitleUkUa = "Ангіна", DescriptionEnUs = "Angina, a.k.a. angina pectoris, is the feeling of chest pain or pressure, usually due to not enough blood flow to the heart muscle." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000065"), TitleEnUs = "Heart attack", TitleUkUa = "Серцевий напад", DescriptionEnUs = "The most serious disease of the heart is the myocardial infaction (a.k.a. \"heart attack\"). Here, parts of the muscle die due to disruption of their essential blood supply. The heart itself utilizes for its own needs approx. 10% of the whole blood pumped through it. This blood flow disruption occurs most ofen due to obstruction of the coronary arteries as a result of atherosclerosis and/or vessel cramp." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000066"), TitleEnUs = "Heart failure", TitleUkUa = "Серцева недостатність", DescriptionEnUs = "Weakness of the pumping function of the heart, leading to easy tiredness, shortness of breath, and in extreme cases - swelling of legs, kidney dysfunction, fluid accumulation in lungs (lung edema). Heart failure is classified in 4 classes (1 - slight, 4 - massive)." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000067"), TitleEnUs = "Cirrhosis", DescriptionEnUs = "A chronic disease of the liver leading to loss-of-function due to long-term damage" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000068"), TitleEnUs = "Fat liver", DescriptionEnUs = "An increased fat amount in the liver." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000069"), TitleEnUs = "Hepatitis viral", DescriptionEnUs = "A chronic liver inflammation due to a viral infection that has not healed - in most cases hepatitis B and C." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000070"), TitleEnUs = "Gallstones", DescriptionEnUs = "The gall bladder is 8-10 cm (3-4 in) long organ, collecting bile and placed on the backside of the liver. In many people, the bile aggregates in smaller or larger stone-like structures, which can remain without complaints or produce serious problems. The bile is a key factor for fat digestion and is excreted from the gall bladder into the bile duct and in the small bowel." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000071"), TitleEnUs = "Hepatitis other", DescriptionEnUs = "A chronic liver inflammation due to causes other than infection." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000072"), TitleEnUs = "Anemia", DescriptionEnUs = "A chronic liver inflammation due to causes other than infection." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000073"), TitleEnUs = "High cholesterol", DescriptionEnUs = "Cholesterol is one the most important components of our cell membranes. It is an essential building block of the body. Human body can produce cholesterol by itself and import it from the food too. Too high blood cholesterol levels contribute to advanced atherosclerosis (narrowing of blood vessels) and strongly increases the risk of heart attack and stroke." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000074"), TitleEnUs = "Blood clots", DescriptionEnUs = "After damage of tissues (e.g. cuts), bleeding is rapidly stopped by the clotting ability of blood. In some cases, however, clotting can be stronger than needed (e.g. mutations, medicines, smoking), which elevates the risk of thrombosis (blood vessel obstruction). This can cause stroke, heart attack and other diseases." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000075"), TitleEnUs = "Brain", DescriptionEnUs = "One of the most complex organs of the body, directly associated with out thinking ability. It weighs 1.2-1.5 kg (2.5-3 lbs), as differences do not correlate with intellect and/or other abilities. It is positioned in the skull cavity, where the bones protect it from damage. It comprizes two halves and multiple folds, builing its cortex. The cortex contains defined areas, associated with vision, sensation, hearing, taste, movement and others. The brain is supplied with blood through four major vessels - two large internal carotid arteries and two smaller vertebral arteries. Disruption of the blood supply of the brain leads to tissue damage called \"stroke\"." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000076"), TitleEnUs = "Leukemia and lymphoma", DescriptionEnUs = "The blood is a fluid tissue, running through our blood vessels (arteries and veins) and transporting oxygen, carbon dioxide and all necessary nutrients through the body. The blood is created in the bone marrow in the core of the bones. Any form of disruption of the blood supply may be fatal for the tissues/organs. Its red color is due to the pigment hem in the protein hemoglobin, containing iron. It fills the red blood cells and binds oxygen and carbon dioxide, allowing exchange of gasses through the body. The lymphatic system is another form of vessel system. Its vessels are much smaller and thinner. The lymphatic system drains colourless fluid (lymph) from the organs and tissues, transporting it to the lymph nodes and then to the veins. Contrary to the blood vessels, lymph vessels are visible only under microscope. Lymph nodes and lymph organs - spleen, thymus and some other - are crucially important as immune system organs, where most of the immune cells reside." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000077"), TitleEnUs = "Pancreas", DescriptionEnUs = "A 15-25 cm (6-10 in) long soft organ, positioned exactly behind the stomach in the abdomen. It has two major fiunctions - firstly, it releases enzymes in the duodenum (the first part of the small intestine (small bowel)) that digest fats and proteins from the food. Secondly, the pancreas releases insulin, which enables glucose (HYPERLINK link to glucose) intake by the cells." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000078"), TitleEnUs = "Liver cancer", TitleUkUa = "Рак печінки", DescriptionEnUs = "A 1-1.5 kg (2-3 lbs) organ, positioned under the right rib arc in the abdomen. It removes multiple toxic substances, supports digestion of fats, metabolizes some medicines and is one of the most active organs of our body." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000079"), TitleEnUs = "Intestine/colon/stomach", DescriptionEnUs = "After food is eaten through the mouth, it enters the oesophagus (food pipe) and reaches the stomach. The food pipe is placed between the lungs, just behind the heart. The stomach is the next food stop, here food digestion starts. From the stomach, filled up with 10% salt acid, the partially processed food is pumped into the small intestine (bowel), where it mixes with the bile and with the pancreatic juice, as the digestion of the food components. The food elements are also absorbed here. Finally, in the large bowel, the food rests are dried and packed to be released in the form of feces. The small bowel is 5-7 m long (16-23 ft), the large bowel 1.2-1.5 meters (4-5 ft)." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000080"), TitleEnUs = "Kidney cancer", DescriptionEnUs = "Paired organs with bean-like shape, weighing 110-170 gr. They are positioned on the back wall of abdomen, aproximately where the lowest ribs bind to spinal cord. The major function of the kidneys is to remove multiple unnecessesary products from the body into urine. Urine is collected in the urinary bladder and released during utrination. Kindeys play a key role to maintain the normal volume of fluids in the body, the level of some critical electrolytes and contribute to the regulation of blood pressure." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000081"), TitleEnUs = "Lung cancer", TitleUkUa = "Рак легенів", DescriptionEnUs = "The lungs are paired organs, placed in the chest cavity. The heart is positioned between them. Both lungs are filled with air, which reaches them through the airways (trachea and bronchi). When a person breathes in, fresh air from outside fills the lungs. Here, the oxygen from the air enters the blood flow and it is further transported through the body. In exchange, carbon dioxide, produced from the tissues as a side product, is transported with the blood to the lungs and is released in the exhaled air. Inability to perform this exchange of oxygen and carbon dioxide may lead in extreme cases to suffocation." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000082"), TitleEnUs = "Prostate & testicles", DescriptionEnUs = "A chestnut-shaped gland, placed exactly under the urinary bladder and above the base of the penis in men. It is crossed through the middle by the male urethra, which transports urine from the bladder through the penis. The prostate secretes components of the sperm, important for the survival of the sperm cells." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000083"), TitleEnUs = "Breast cancer", TitleUkUa = "Рак молочної залози", TitleBgBg = "Рак на гърдата", DescriptionEnUs = "A gland positioned on the front chest wall. Contrary to the widely spread opinion, men have breast glands as well, although they are tiny. In some rare cases of hormonal problems they can grow in men as well. Around 1% of all cases of the most common malignant disease of the breast - breast cancer, affect men." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000084"), TitleEnUs = "Womb & cervical cancer" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000085"), TitleEnUs = "Remission", TitleUkUa = "Ремісія", DescriptionEnUs = "A state of absence of symptoms/disease activity of a chronic disease that cannot be cured. As a rule, in all type of cancers the medicine never speaks of a \"cured patient\", but of a \"patient in complete remission\"."},
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000086"), TitleEnUs = "Thyroid disease", DescriptionEnUs = "A gland on the neck, positioned in front of the air pipe (trachea) and exactly under the Adam`s apple. It has one left and one right part (lobe). It produces thyroid hormones (L-thyroxin (T4) and triiodthyronin (T3)), which function as metabolic regulators, stimulating body growth and metabolism." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000087"), TitleEnUs = "Diabetes", TitleUkUa = "Діабет", DescriptionEnUs = "A major energy source for all tissues (and their cells) in our body is glucose. In diabetes, the function of the protein insulin (produced by the pancreas) is affected and insulin is important for glucose transport into the cells. Without insulin, cells \"swim\" in glucose, but cannot use it. When not properly treated, diabetes is accompanied with high glucose concentration in the blood." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000088"), TitleEnUs = "Adrenal disease", DescriptionEnUs = "An endocrine gland with triangular shape, producing cortisol, epinephrine (stress hormones) and some other. There are two of them in the body, they do not exceed 3-4 cm and they are positioned on the back of the abdomen, exactly above the upper pole of the kidney." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000089"), TitleEnUs = "Liver transplantation", DescriptionEnUs = "A 1-1.5 kg (2-3 lbs) organ, positioned under the right rib arc in the abdomen. It removes multiple toxic substances, supports digestion of fats, metabolizes some medicines and is one of the most active organs of our body." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000090"), TitleEnUs = "Kidney transplantation", DescriptionEnUs = "Paired organs with bean-like shape, weighing 110-170 gr. They are positioned on the back wall of abdomen, aproximately where the lowest ribs bind to spinal cord. The major function of the kidneys is to remove multiple unnecessesary products from the body into urine. Urine is collected in the urinary bladder and released during utrination. Kindeys play a key role to maintain the normal volume of fluids in the body, the level of some critical electrolytes and contribute to the regulation of blood pressure." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000091"), TitleEnUs = "Bone marrow transplant", DescriptionEnUs = "Bone marrow is a soft tissue filling the cavity in the middle of each bone. It produces all forms of blood cells. In cases of blood cancers, as well as during intensive anti-cancer chemotherapy, the bone marrow can get damaged and new, external bone marrow from healthy donor will be transfered. The new bone marrow expands and replaces the old one. There are risks, associated with this procedure. During transplantation, the body remains without immune protection for several day/weeks. Further on, the new bone marrow may start attacking the tissues of the host (graft-versus-host syndrome). To avoid it, immunosuppressive therapy may be applied and it weakens the immune system." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000092"), TitleEnUs = "Dust", TitleUkUa = "Пил", DescriptionEnUs = "Allergy is an excessive immune reaction against typically harmless external substances, leading to skin rashes, running nose, sneezing, swelling. Home dust is one of th emost common allergies." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000093"), TitleEnUs = "Animals", TitleUkUa = "Тварини", DescriptionEnUs = "Allergy is an excessive immune reaction against typically harmless external substances, leading to skin rashes, running nose, sneezing, swelling. Allergies against animals are generally specific for a certain species and to specific element of their bodies - fur, saliva. Such allergies may develop even after a period without problems." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000094"), TitleEnUs = "Hay fever", DescriptionEnUs = "Allergy is an excessive immune reaction against typically harmless external substances, leading to skin rashes, running nose, sneezing, swelling. Hay fever is a very common allergy, which occurs in a period of the year when specific plant blossoms - most commonly end of sprint/beginning of summer." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000095"), TitleEnUs = "Food", DescriptionEnUs = "Allergy is an excessive immune reaction against typically harmless external substances, leading to skin rashes, running nose, sneezing, swelling. Hay fever is a very common allergy, which occurs in a period of the year when specific plant blossoms - most commonly end of sprint/beginning of summer." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000096"), TitleEnUs = "Gluten", DescriptionEnUs = "An autoimmune condition triggered by gluten found in multiple cereal grain and their products. Less than 1% of all people have this problem." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000098"), TitleEnUs = "Uterus removed"},
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000099"), TitleEnUs = "Heavy menstrual bleeding" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000100"), TitleEnUs = "Menopause" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000101"), TitleEnUs = "Cancer in remission" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000102"), TitleEnUs = "Heartburn/reflux", DescriptionEnUs = "Burning sensation in the chest and upper abdomen, sometimes accompanied with acid taste in the throat. Its is due to reflux of stomach acid into the food pipe." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000103"), TitleEnUs = "Lactose intolerance", DescriptionEnUs = "Reaction to lactose (milk sugar) due to the lack of a protein (enzyme) in our bowel, which helps digest the sugar. Lactose remains in the food and drags more water in the feces, leading to diarrhea. Some people may develop mild forms of lactose deficiency if not consuming milk product for a longer period of time." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000104"), TitleEnUs = "Ulcer/Gastritis", DescriptionEnUs = "An inflammation/wound of the stomach lining." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000105"), TitleEnUs = "Removed (part or full) stomach or bowel" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000106"), TitleEnUs = "Constipation or diarrhia" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000107"), TitleEnUs = "Diverticulitis", DescriptionEnUs = "An inflammation/wound of the stomach lining." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000108"), TitleEnUs = "Crohn's disease and ulcerative colitis", DescriptionEnUs = "Inflammatory bowel diseases with autoimmune origin accompanied with abdominal pain, dirarhea (sometimes bloody), fever and weight loss." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000109"), TitleEnUs = "Family polyposis", DescriptionEnUs = "An abnormal growth of tissue from the wall of the bower called polyp." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000110"), TitleEnUs = "Stroke", TitleUkUa = "Інсульт", DescriptionEnUs = "The brain is supplied with blood through four major vessels - two large internal carotid arteries and two smaller vertebral arteries. Disruption of the blood supply of the brain leads to tissue damage called \"stroke\". It might be very mild, but it can be heavy and life threatening. Depending on the vessel affected, symptoms may largely vary. They may affect movement, sensation (all forms - hearing, vison, taste, touch), speech, very often asymetrically." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000111"), TitleEnUs = "Parkinson`s", DescriptionEnUs = "A brain disease caused by the loss of nerve cells in substantia nigra - an area, involved in movement control. Patients develop problems performing daily tasks (e.g. shaking) and walking, which get worse with time." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000112"), TitleEnUs = "Dementia (incl. Alzheimer`s)", DescriptionEnUs = "A group of diseases associated with continuous decrease of intellectual capacity due to loss of nerve cells. There are multiple forms - Alzheimer`s disease, vascular dementia (slow obstruction of blood vessels), Korsakoff`s syndrome (alcohol dementia) and others. Very rarely, there can be genetic background (can be inherited)." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000113"), TitleEnUs = "Multiple sclerosis (MS)", DescriptionEnUs = "An autoimmune disease (a disease in which the body`s own immune system attacks its parts) of the brain and the spinal cord leading to damage in the white matter (the connection between neuronal cells). It leads predominantly to disturbance of movement, but can also cause sensational disturbances such as double vision and altered sense of touch. It can progress in individual attacks without symptoms in between. With time, however, permanent progressing disability may occur." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000114"), TitleEnUs = "Peripheral nerve disease", DescriptionEnUs = "A group of diseases, associated with peripheral nerve damage - damage outside the brain and the spinal cord. It may affect movement, sensation, but also the function of some organs. The causes can be genetic, alcohol, diabetes, medication (such as chemotherapy), radiotherapy, trauma, infections and others." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000115"), TitleEnUs = "OCD", DescriptionEnUs = "The abbreviations stands for \"obsessive - compulsive disorder\". It is characterized with penetrant repetitive thoughts (obsessive thoughts) and repetitive ritual behavior (compulsive behavior)." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000116"), TitleEnUs = "Depression", TitleUkUa = "Депресія", DescriptionEnUs = "A mental disorder that is characterized with continuous low mood most of the time. It is accompanied with low self-esteem, loss of interest for most activities, low energy, sometimes physical pains without identifiable disease." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000117"), TitleEnUs = "Psychosis", DescriptionEnUs = "A mental disorder that is characterized with decreased ability to understand reality, often manisfetsed with altered speech and behavior. It can be associated with symptoms of some other mental disorders, which makes diagnosis very difficult. Further, it can be associated with false beliefs, hearing voices and seeing non-existent events (hallucinations). Schizophrenia is commonly mixed with \"dissociative identity disorder\" (a.k.a. multiple personality disorder). Some family predisposition can be observed in these diseases." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000118"), TitleEnUs = "Manic depression (bipolar disorder)", DescriptionEnUs = "A mental disorder characterized with a swing between depressive episodes and anormally elevated mood." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000119"), TitleEnUs = "Down`s syndrome", DescriptionEnUs = "A.k.a. Trisomy 21. Instead of the normal 2 sets of 21th chromosome (one from the mother and one from the father), the cells of the body contain 3 sets chromosome 21. It is associated with variable levels of intellectual disability (IQ 30-70), characteristic face expression and short neck and lower height, common congenital heart defects, occasional hearing and vision problems, and other." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000120"), TitleEnUs = "Cystic fibrosis", DescriptionEnUs = "A genetic disorder, inherited in an recessive manner (the disorder can remain as a mutation silent for several generations), leading to a defect of a protein, allowing transport of chloride ions (and water) into digestive fluid and mucus, producing saltier sweat too. Mucus and digestive fluids become much thicker, leading to increased retention and damage in the lungs (breathing problems) and pancreas, and not so often liver and kidneys. The patients have often diminished growth and commonly recurrent lung infections." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000121"), TitleEnUs = "Gout", DescriptionEnUs = "A joint pain, swelling and redness in the joints (very common is the base of the big toe) due to accumulation of uric acid, which levels in blood are high. It occurs in people consuming a lot of sea food, meat, are overweight and/or drink alcohol." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000122"), TitleEnUs = "Other genetic disorder" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000123"), TitleEnUs = "Other metabolic disorder" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000124"), TitleEnUs = "HIV/AIDS", DescriptionEnUs = "The human immunodeficiency viruses (HIV) are retroviruses infecting human T-lymphocytes (among others). They are transmitted via blood, drung addict needles, pregnancy or sexual intercourse. HIV may stay unnoticed for a long time, but can manifest at any time with the symptoms of AIDS (acquired immunodeficiency syndrome), characterized with frequent infections by normally harmless viruses and bacteria, high cancer incidence, rare cancers (e.g. Kaposhi`s sarcoma). Successful treatment can delay or completely stop the progression into AIDS." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000125"), TitleEnUs = "Chronic joint pain", DescriptionEnUs = "Here belong most of the non-specific joint complaints, which are not autoimmune, gout-related or rheuma arthririts-related." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000126"), TitleEnUs = "Osteoporosis", DescriptionEnUs = "Bone weakness and loss of bone material, leading to easier fractures, especially in the vertebra (the spinal column building blocks). Osteoporosis is often observed in seniors, but also in the course of some diseases." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000127"), TitleEnUs = "Rheumatism", DescriptionEnUs = "An autoimmune disease (a disease in which the body`s own immune system attacks its parts) of the joints (most often the small joints of the hand). They are warm, swollen and painful and with time may get deformed. Affects most often seniors." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000128"), TitleEnUs = "Psoriasis", DescriptionEnUs = "An autoimmune disease (a disease in which the body`s own immune system attacks its parts) of the skin leading to the formation of skin patches, which are itchy, red, dry and scaly. It can affect joints as well (rarely)." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000129"), TitleEnUs = "Lupus", DescriptionEnUs = "An autoimmune disease (a disease in which the body`s own immune system attacks its parts) leading commonly to skin changes such as the specific \"butterfly rash\" around the nose. It can be mild, but also very serious in its course. It may affect various organs such as kidneys, heart, brain, can lead to swallen lymph nodes, fever, loss of hair (less common)." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000130"), TitleEnUs = "Other autoimmune", DescriptionEnUs = "In autoimmune diseases, the immune system attacks its own tissues. Multiple conditions are autoimmune - lupus, psoriasis, multiple sclerosis, Crohn, HUK and many more." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000131"), TitleEnUs = "Gum disease", DescriptionEnUs = "An inflammation of the gums leading to bleeding and later on - to the gum pulling away from the tooth and eventually tooth loss." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000132"), TitleEnUs = "Falling teeth" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000133"), TitleEnUs = "High eye pressure & glaucoma", DescriptionEnUs = "Increased eye pressure can remain asymptomatic, but can lead to glaucoma, which is a process of damage of the eye nerve and partial or complete loss of vision. The reasons for glaucoma, however, are not always related to high eye pressure." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000134"), TitleEnUs = "Emphysema", TitleUkUa = "Емфізема" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000135"), TitleEnUs = "Impaired vision" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000136"), TitleEnUs = "Other eye disease" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000137"), TitleEnUs = "Hearing loss" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000138"), TitleEnUs = "Diseases of the nose", DescriptionEnUs = "The sinus is an air-filled cavity in the bone around the nose, reducing the weight of the bone. It is connected with the nasal cavity and can get infected (sinusitis). Most offen affected by infections are the maxillary and the frontal sinuses. Not very infection of the nasal cavity leads to sinusitis." },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000139"), TitleEnUs = "Myocardial infarction" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000140"), TitleEnUs = "Early myocardial infarction & stroke (<55)" },
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000141"), TitleEnUs = "Allergies", TitleUkUa = "Алергія"},
                new EncyclopediaEntity { Id = new Guid("00000000-0000-0000-0000-000000000142"), TitleEnUs = "familial polyposis (FAP)" },
